"use strict";

const t1 = "/a/b/c?jwt=sakdskf.asdf.a.sdf";
const t2 = "/a/b/c?some=parm&jwt=sakdskf.asdf.a.sdf";
const t3 = "/a/b/c?some=parm&jwt=sakdskf.asdf.a.sdf&diff=parm";

const t = (test) => {
  test = test.replace(/(&|\?)+jwt=.*?&/,"&");
  test = test.replace(/(&|\?)+jwt=.*?$/,"");
  return test;
}
console.log(t(t1));
console.log(t(t2));
console.log(t(t3));
// console.log(t(t4));
